// frontend/src/components/Results.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Results.css'; // Ensure this CSS file includes the styling for credibility bars

const Results = ({ posts }) => {
  const [credibilityScores, setCredibilityScores] = useState([]);

  useEffect(() => {
    if (posts && posts.length > 0) {
      console.log("Posts received in Results component:", posts); // Log the posts received as props
      // Fetch credibility scores from the SageMaker endpoint
      const fetchCredibilityScores = async () => {
        try {
          const response = await axios.post("your-sagemaker-endpoint-url", { posts });
          console.log("Credibility scores response:", response.data); // Log the response from SageMaker
          setCredibilityScores(response.data);
        } catch (error) {
          console.error("Error fetching credibility scores:", error);
        }
      };
      fetchCredibilityScores();
    }
  }, [posts]);

  return (
    <div>
      {posts && posts.length > 0 ? (
        posts.map((post, index) => (
          <div key={post.id} className="post">
            <div className="post-title">{post.title}</div>
            <div className="credibility-bar">
              {Array(5).fill().map((_, i) => (
                <span key={i} className={`bar ${credibilityScores[index] >= (i+1)/5 ? 'filled' : ''}`}></span>
              ))}
            </div>
          </div>
        ))
      ) : (
        <div>No posts to display</div>
      )}
    </div>
  );
};

export default Results;
